### 1.2 Codable

```Swift
import UIKit

struct Response: Codable {
    let resultCount: Int
    let tracks: [Track]

    enum CodingKeys: String, CodingKey {
        case resultCount
        case tracks = "results"
    }
}

struct Track: Codable {
    let title: String
    let artistName: String
    let thumbnailPath: String

    enum CodingKeys: String, CodingKey {
        case title = "trackName"
        case artistName
        case thumbnailPath = "artworkUrl100"
    }
}
func encodeData(){
    let trackObject = Response(resultCount: 3, tracks: [
        Track(title: "hello",
              artistName: "heeloo",
              thumbnailPath: "eed"),
        Track(title: "hello",
              artistName: "heeloo",
              thumbnailPath: "eed"),
        Track(title: "hello",
              artistName: "heeloo",
              thumbnailPath: "eed")
    ])
    let encodeData = try? JSONEncoder().encode(trackObject)
    print("EncodeData: \(encodeData)")
    decodeData(decodeData: encodeData!)
}
encodeData()

func decodeData(decodeData: Data) {
    //let resultString = String(data: decodeData, encoding: .utf8)
    //print("Data in String: \(resultString)")

    do {
        let decoder = JSONDecoder()
        let response = try decoder.decode(Response.self, from: decodeData)
        print("\(response)")
    } catch let error {
        print(error.localizedDescription)
    }
}
